import React from 'react';
import MaterialTable from 'material-table';

export default function MaterialTableDemo() {

    const [state, setState] = React.useState({
        columns: [
            { title: 'Nome', field: 'nome' },
            { title: 'Preço', field: 'preco', type: 'numeric' },
            { title: 'Area Útil', field: 'areaUtil', type: 'numeric' },
            { title: 'Valor/m2', field: 'valorm2', type: 'numeric' },
            { title: 'Bairro', field: 'bairro' },
            {
                title: 'Cidade',
                field: 'cidade',
                lookup: { 34: 'Cascavel' },
            },
            { title: 'Link', field: 'link' },
        ],
        data: [
            { nome: 'Ap Modelo', preco: 200000, areaUtil: 60, valorm2: 0, bairro: 'Cancelli', cidade: 34, link: 'Link' },
        ],
    });

    let calcValorM2 = state.data.preco / state.data.areaUtil;
    setState.data.valorm2 = calcValorM2;

    return (
        <MaterialTable
            title="Editable Example"
            columns={state.columns}
            data={state.data}
            editable={{
                onRowAdd: (newData) =>
                    new Promise((resolve) => {
                        setTimeout(() => {
                            resolve();
                            setState((prevState) => {
                                const data = [...prevState.data];
                                data.push(newData);
                                return { ...prevState, data };
                            });
                        }, 600);
                    }),
                onRowUpdate: (newData, oldData) =>
                    new Promise((resolve) => {
                        setTimeout(() => {
                            resolve();
                            if (oldData) {
                                setState((prevState) => {
                                    const data = [...prevState.data];
                                    data[data.indexOf(oldData)] = newData;
                                    return { ...prevState, data };
                                });
                            }
                        }, 600);
                    }),
                onRowDelete: (oldData) =>
                    new Promise((resolve) => {
                        setTimeout(() => {
                            resolve();
                            setState((prevState) => {
                                const data = [...prevState.data];
                                data.splice(data.indexOf(oldData), 1);
                                return { ...prevState, data };
                            });
                        }, 600);
                    }),
            }}
        />
    );
}
