import React, { useEffect, useState } from 'react';
import MaterialTable from 'material-table';
import { Link } from '@material-ui/core';

import api from '../../services/api';


export default function MaterialTableDemo() {

    const [state, setState] = React.useState({
        columns: [
            { title: 'Nome', field: 'nome' },
            { title: 'Preço', field: 'preco', type: 'currency' },
            { title: 'Area Útil', field: 'areaUtil', type: 'numeric' },
            { title: 'Valor/m2', field: 'valorm2', type: 'currency' },
            { title: 'Bairro', field: 'bairro' },
            { title: 'Cidade', field: 'cidade' },
            { title: 'Link', field: 'link' },
        ]
    });

    const [dataApi, setDataApi] = useState([]);

    useEffect(() => {
        (async function loadImoveis() {
            const response = await api.get('/imovel');
            setDataApi(response.data)
            //console.log(response)
        }());

    }, [])
    //console.log(dataApi)
    var imoveis = dataApi.map((ap, index) => {
        let valorm2 = ap.preco / ap.areaUtil
        let linkHref = ap.link;

        return {
            nome: `${ap.nome}`,
            preco: `${ap.preco.toLocaleString('pt-BR', { style: 'currency', currency: 'BRL' })}`,
            areaUtil: `${ap.areaUtil} m2`,
            valorm2: `${valorm2.toLocaleString('pt-BR', { style: 'currency', currency: 'BRL' })}`,
            bairro: `${ap.bairro}`,
            cidade: 'cascavel',
            link: <Link target='_blank' rel='noopener' href={linkHref} >Link do Anúncio</Link>
        }
    })
    //console.log(imoveis)
    return (
        <MaterialTable
            title="Editable Example"
            columns={state.columns}
            data={imoveis}
            editable={{
                onRowAdd: (newData) =>
                    new Promise((resolve) => {
                        setTimeout(() => {
                            resolve();
                            setDataApi((prevState) => {
                                const data = [...prevState];
                                data.push(newData);
                                return { ...prevState, data };
                            });
                        }, 600);
                    }),
                onRowUpdate: (newData, oldData) =>
                    new Promise((resolve) => {
                        setTimeout(() => {
                            resolve();
                            if (oldData) {
                                setState((prevState) => {
                                    const data = [...prevState.data];
                                    data[data.indexOf(oldData)] = newData;
                                    return { ...prevState, data };
                                });
                            }
                        }, 600);
                    }),
                onRowDelete: (oldData) =>
                    new Promise((resolve) => {
                        setTimeout(() => {
                            resolve();
                            setState((prevState) => {
                                const data = [...prevState.data];
                                data.splice(data.indexOf(oldData), 1);
                                return { ...prevState, data };
                            });
                        }, 600);
                    }),
            }}
        />
    );
}
