import React, { useState, useEffect } from 'react';
//material core
import Icon from "@material-ui/core/Icon";
//components
import GridContainer from '../../components/Grid/GridContainer';
import GridItem from '../../components/Grid/GridItem';
import Card from '../../components/Card/Card';
import CardHeader from '../../components/Card/CardHeader';
import CardIcon from '../../components/Card/CardIcon';
import CardFooter from '../../components/Card/CardFooter';
import CardBody from '../../components/Card/CardBody';
import Button from '../../components/CustomButtons/Button';
import TableShow from '../../components/Table/TableShowSinapi';
// @material-ui/icons
import Landscape from '@material-ui/icons/Landscape';
//typografia
import Danger from '../../components/Typography/Danger';
import Warning from '../../components/Typography/Warning';
//form
import OutlinedInput from '@material-ui/core/OutlinedInput';
import FormControl from '@material-ui/core/FormControl';
import InputLabel from '@material-ui/core/InputLabel';

import api from '../../services/api';

function SearchInsumo() {

    const [input, setInput] = useState('');
    const [filtro, setFiltro] = useState('');

    async function handleSearch(e) {
        e.preventDefault();
        try {
            setFiltro(input);
        } catch (err) {
            alert(`Falha na busca, tente novamente:\n ${err}`);
        }
    }

    async function loadInsumos() {

        const response = await api.get(`/insumos/search?input=${props.filtro}&page=1`);
        setState((prevState) => {
            const data = prevState.data;
            data.pop();
            data.push(response.data);
            //console.log(data);
            return { ...prevState };
        });
    }

    return (
        <div>
            <GridContainer>
                <GridItem xs={12} sm={12} md={12}>
                    <Card>
                        <CardHeader color="success" stats icon>
                            <CardIcon color="success">
                                <Icon> <Landscape /></Icon>
                            </CardIcon>
                            <p>Pesquisa de Insumos</p>
                        </CardHeader>
                        <CardBody>
                            <form >
                                <FormControl fullWidth variant="outlined" margin='normal'>
                                    <InputLabel htmlFor="outlined-filtro">Descri√ß√£o ou C√≥digo</InputLabel>
                                    <OutlinedInput
                                        id="outlined-filtro"
                                        labelWidth={60}
                                        value={input}
                                        onChange={e => setInput(e.target.value)}
                                    />
                                </FormControl>

                                <FormControl fullWidth variant="outlined" margin='normal'>
                                    <InputLabel htmlFor="outlined-filtro">Banco</InputLabel>
                                    <OutlinedInput
                                        id="outlined-filtro"
                                        labelWidth={60}
                                    />
                                </FormControl>
                                <Button onClick={() => loadInsumos()} color="primary" block={true} size="lg">Buscar</Button>
                            </form>
                            <TableShow filtro={filtro} />
                        </CardBody>
                        <CardFooter stats>
                            <div >
                                <Danger>
                                    <Warning>ex</Warning>
                                </Danger>
                            </div>
                        </CardFooter>
                    </Card>
                </GridItem>
            </ GridContainer>
        </div>
    )
}

export default SearchInsumo;
