import React, { useEffect, useState } from 'react';
import MaterialTable from 'material-table';
import { Link } from '@material-ui/core';

import api from '../../services/api';


export default function MaterialTableDemo() {

    const [state, setState] = React.useState({
        columns: [
            { title: 'Nome', field: 'nome' },
            { title: 'Preço', field: 'preco' },
            { title: 'Area Útil', field: 'areaUtil', type: 'numeric', align: 'center' },
            { field: 'unidade', editable: 'never', sorting: false, readonly: true, align: 'left' },
            { title: 'Valor/m2', field: 'valorm2', editable: 'never' },
            { title: 'Bairro', field: 'bairro' },
            { title: 'Cidade', field: 'municipio' },
            { title: 'Categoria', field: 'categoria' },
            { title: 'Tipo', field: 'tipo' },
            { title: 'Link', field: 'link' },
        ],
        data: []
    });

    const [dataApi, setDataApi] = useState([]);

    useEffect(() => {
        loadImoveis();
    }, [state.data])

    async function loadImoveis() {
        const response = await api.get('/imovel');
        setState((prevState) => {
            const data = prevState.data;
            data.push(response.data);
            return { ...prevState, data };
        });
        var imoveis = state.data[0].map((ap, index) => {
            let valorm2 = ap.preco / ap.areaUtil
            let linkHref = ap.link;
            return {
                id: `${ap._id}`,
                nome: `${ap.nome}`,
                preco: `${ap.preco.toLocaleString('pt-br', { style: 'currency', currency: 'BRL' })}`,
                areaUtil: `${ap.areaUtil}`,
                unidade: 'm2',
                valorm2: `${valorm2.toLocaleString('pt-br', { style: 'currency', currency: 'BRL' })}`,
                bairro: `${ap.bairro}`,
                municipio: `${ap.municipio}`,
                categoria: `${ap.categoria}`,
                tipo: `${ap.tipo}`,
                link: <Link target='_blank' rel='noopener' href={linkHref} >Link do Anúncio</Link>
            }
        })
        setDataApi(imoveis);
    };

    async function handleDelete(oldData) {
        try {
            let id = oldData.id;
            await api.delete(`/imovel/${id}`)
            setDataApi(imoveis.filter(imovel => imovel.id !== id));
        } catch (err) {
            alert('erro ao deletar:' + err)
        }
    }
    return (
        <MaterialTable
            title="Relátorio"
            columns={state.columns}
            data={dataApi}
            editable={{
                onRowAdd: (newData) =>
                    new Promise((resolve, reject) => {
                        try {
                            resolve(api.post('/imovel', newData))
                        } catch (err) {
                            console.error('Erro' + err);
                            return reject('erro' + err);
                        }
                    }),
                onRowUpdate: (newData, oldData) =>
                    console.log('update'),
                onRowDelete: (oldData) =>
                    new Promise((resolve) => {
                        setTimeout(() => {
                            resolve();
                            setState((prevState) => {
                                const data = prevState.data;
                                data.splice(data.indexOf(oldData), 1);
                                return { ...prevState, data };
                            });
                            handleDelete(oldData);
                        }, 600);
                    }),
            }}
        />
    );
}
