import React, { useEffect, useState } from 'react';
import MaterialTable from 'material-table';
import { Link } from '@material-ui/core';

import api from '../../services/api';


export default function MaterialTableDemo() {

    const [state, setState] = React.useState({
        columns: [
            { title: 'Nome', field: 'nome' },
            { title: 'Preço', field: 'preco', type: 'numeric' },
            { title: 'Area Útil', field: 'areaUtil', type: 'numeric' },
            { title: 'Valor/m2', field: 'valorm2', type: 'numeric' },
            { title: 'Bairro', field: 'bairro' },
            {
                title: 'Cidade',
                field: 'cidade',
                lookup: { 34: 'Cascavel' },
            },
            { title: 'Link', field: 'link' },
        ],
        data: [
            { nome: 'Ap Modelo', preco: 200000, areaUtil: 60, valorm2: 0, bairro: 'Cancelli', cidade: 34, link: 'Link' },
        ],
    });

    const [dataApi, setDataApi] = useState([]);

    useEffect(() => {
        (async function loadImoveis() {
            const response = await api.get('/imovel');
            setDataApi(response.data)
        }());

    }, [])

    var imoveis = dataApi.map((ap, index) => {
        let valorm2 = ap.preco / ap.areaUtil
        let linkHref = ap.link;
        console.log(imoveis)
        return [
            `${ap.nome}`,
            `${ap.preco.toLocaleString('pt-BR', { style: 'currency', currency: 'BRL' })}`,
            `${ap.areaUtil} m2`,
            `${valorm2.toLocaleString('pt-BR', { style: 'currency', currency: 'BRL' })}`,
            `${ap.bairro}`,
            `Cascavel`,
            <Link target='_blank' rel='noopener' href={linkHref} >Link do Anúncio</Link>
        ]
    })

    return (
        <MaterialTable
            title="Editable Example"
            columns={state.columns}
            data={imoveis}
            editable={{
                onRowAdd: (newData) =>
                    new Promise((resolve) => {
                        setTimeout(() => {
                            resolve();
                            setState((prevState) => {
                                const data = [...prevState.data];
                                data.push(newData);
                                return { ...prevState, data };
                            });
                        }, 600);
                    }),
                onRowUpdate: (newData, oldData) =>
                    new Promise((resolve) => {
                        setTimeout(() => {
                            resolve();
                            if (oldData) {
                                setState((prevState) => {
                                    const data = [...prevState.data];
                                    data[data.indexOf(oldData)] = newData;
                                    return { ...prevState, data };
                                });
                            }
                        }, 600);
                    }),
                onRowDelete: (oldData) =>
                    new Promise((resolve) => {
                        setTimeout(() => {
                            resolve();
                            setState((prevState) => {
                                const data = [...prevState.data];
                                data.splice(data.indexOf(oldData), 1);
                                return { ...prevState, data };
                            });
                        }, 600);
                    }),
            }}
        />
    );
}
