import React from 'react'
import api from '../../services/api'
import { Link } from '@material-ui/core';

const valorMax = async () => {
    const imoveis = [];
    const response = await api.get('/imovel');
    response.data.map((imovel) => {
        imoveis.push(imovel)
    })

    const arrayPrecos = await imoveis.map((ap) => ap.preco)
    const valorMax = Math.max(...arrayPrecos).toLocaleString('pt-BR', { style: 'currency', currency: 'BRL' });

    return (
        <span>{valorMax}</span>
    )
}

