import React, { useState } from 'react';
//material core
import Icon from "@material-ui/core/Icon";
import { makeStyles } from '@material-ui/core/styles';
import InputLabel from '@material-ui/core/InputLabel';
import Select from '@material-ui/core/Select';
import MenuItem from '@material-ui/core/MenuItem';
//components
import GridContainer from '../../components/Grid/GridContainer';
import GridItem from '../../components/Grid/GridItem';
import Card from '../../components/Card/Card';
import CardHeader from '../../components/Card/CardHeader';
import CardIcon from '../../components/Card/CardIcon';
import CardBody from '../../components/Card/CardBody';
import Button from '../../components/CustomButtons/Button';
import TableShow from '../../components/Table/TableShowSinapi';
// @material-ui/icons
import Landscape from '@material-ui/icons/Landscape';
//form
import OutlinedInput from '@material-ui/core/OutlinedInput';
import FormControl from '@material-ui/core/FormControl';


import api from '../../services/api';

import './styles.css';

const useStyles = makeStyles((theme) => ({
    formControl: {
        margin: theme.spacing(1),
        minWidth: 120,
    },
    selectEmpty: {
        marginTop: theme.spacing(2),
    },
}));



function SearchInsumo() {
    const classes = useStyles();

    const [banco, setBanco] = React.useState('');

    const handleChange = (event) => {
        setBanco(event.target.value);
    };

    const [input, setInput] = useState('');

    const [insumosData, setInsumosData] = useState();


    async function loadInsumos() {
        const response = await api.get(`/insumos/search?input=${input}&page=1`);
        setInsumosData(response.data);
    }

    return (
        <div className="content">
            <GridContainer>
                <GridItem xs={12} sm={12} md={12}>
                    <Card>
                        <CardHeader color="success" stats icon>
                            <CardIcon color="success">
                                <Icon> <Landscape /></Icon>
                            </CardIcon>
                            <p>Pesquisa de Insumos</p>
                        </CardHeader>
                        <CardBody>
                            <form className="form">
                                <FormControl variant="outlined" margin='normal' >
                                    <InputLabel htmlFor="outlined-filtro">c√≥digo ou Descri√ß√£o</InputLabel>
                                    <OutlinedInput
                                        id="outlined-filtro"
                                        labelWidth={80}
                                        value={input}
                                        onChange={e => setInput(e.target.value)}
                                    />
                                </FormControl>

                                <FormControl variant="outlined" className={classes.formControl}>
                                    <InputLabel id="demo-simple-select-outlined-label">Banco</InputLabel>
                                    <Select
                                        labelId="demo-simple-select-outlined-label"
                                        id="demo-simple-select-outlined"
                                        value={banco}
                                        onChange={handleChange}
                                        label="Banco"
                                    >
                                        <MenuItem value="">
                                            <em>None</em>
                                        </MenuItem>
                                        <MenuItem value={10}>Ten</MenuItem>
                                        <MenuItem value={20}>Twenty</MenuItem>
                                        <MenuItem value={30}>Thirty</MenuItem>
                                    </Select>
                                </FormControl>

                                <Button onClick={loadInsumos} color="primary" block={true} size="lg">Buscar</Button>
                            </form>
                            <div className="table">
                                <TableShow insumos={insumosData} />
                            </div>
                        </CardBody>
                    </Card>
                </GridItem>
            </ GridContainer>
        </div>
    )
}

export default SearchInsumo;
