import React from 'react'
import { Link } from '@material-ui/core';
const resultSanitized = require('../resultSanitized/resultSanitized')

const arrayPrecos = resultSanitized.map((ap) => ap.preco)
const valorMax = Math.max(...arrayPrecos).toLocaleString('pt-BR', { style: 'currency', currency: 'BRL' });

const dataTab = resultSanitized.map((ap, index) => {
    let valorm2 = ap.preco / ap.areaUtil
    let linkHref = ap.link;

    return [
        `${index + 1}`,
        `${ap.nome}`,
        `${ap.preco.toLocaleString('pt-BR', { style: 'currency', currency: 'BRL' })}`,
        `${ap.areaUtil} m2`,
        `${valorm2.toLocaleString('pt-BR', { style: 'currency', currency: 'BRL' })}`,
        `${ap.bairro}`,
        `<a target="_blank" rel="noopener" href=${linkHref} >Link do Anúncio</a>`
    ]
})

export { dataTab, valorMax }
