import React, { useState, useEffect } from 'react';
import PropTypes from 'prop-types';
import { makeStyles } from '@material-ui/core/styles';
import Box from '@material-ui/core/Box';
import Collapse from '@material-ui/core/Collapse';
import IconButton from '@material-ui/core/IconButton';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Typography from '@material-ui/core/Typography';
import Paper from '@material-ui/core/Paper';
import KeyboardArrowDownIcon from '@material-ui/icons/KeyboardArrowDown';
import KeyboardArrowUpIcon from '@material-ui/icons/KeyboardArrowUp';

import api from '../../services/api';

const useRowStyles = makeStyles({
    root: {
        '& > *': {
            borderBottom: 'unset',
        },
    },
});

/*
function createData(_id,codigo, descricao, tipo, unidade, data, valor) {
    return {
        _id,
        codigo,
        descricao,
        tipo,
        unidade,
        data,
        valor,
    };
}
*/

function Row(props) {
    const { row, history } = props;
    const [open, setOpen] = React.useState(false);
    const classes = useRowStyles();

    return (
        <React.Fragment>
            <TableRow className={classes.root}>
                <TableCell>
                    <IconButton aria-label="expand row" size="small" onClick={() => setOpen(!open)}>
                        {open ? <KeyboardArrowUpIcon /> : <KeyboardArrowDownIcon />}
                    </IconButton>
                </TableCell>
                <TableCell component="th" scope="row">{row.CODIGO_DA_COMPOSICAO}</TableCell>
                <TableCell>{row.DESCRICAO_DA_COMPOSICAO}</TableCell>
                <TableCell align="center">{row.SIGLA_DA_CLASSE}</TableCell>
                <TableCell align="center">{row.UNIDADE}</TableCell>
                <TableCell align="right">{row.createdAt}</TableCell>
                <TableCell align="right">{row.CUSTO_TOTAL}</TableCell>
            </TableRow>
            <TableRow>
                <TableCell style={{ paddingBottom: 0, paddingTop: 0 }} colSpan={7}>
                    <Collapse in={open} timeout="auto" unmountOnExit>
                        <Box margin={1}>
                            <Typography variant="h6" gutterBottom component="div">
                                Detalhamento
                            </Typography>
                            <Table size="small" aria-label="purchases">
                                <TableHead>
                                    <TableRow>
                                        <TableCell>Tipo Item</TableCell>
                                        <TableCell>Código</TableCell>
                                        <TableCell>Descrição</TableCell>
                                        <TableCell>Tipo</TableCell>
                                        <TableCell>Unidade</TableCell>
                                        <TableCell align="right">Valor Unit.</TableCell>
                                        <TableCell align="right">Coeficiente</TableCell>
                                        <TableCell align="right">Valor(R$)</TableCell>
                                    </TableRow>
                                </TableHead>
                                <TableBody>
                                    {history.map((historyRow) => (
                                        <TableRow key={historyRow.codigo}>
                                            <TableCell component="th" scope="row">
                                                {historyRow.tipoItem}
                                            </TableCell>
                                            <TableCell>{historyRow.codigo}</TableCell>
                                            <TableCell>{historyRow.descricao}</TableCell>
                                            <TableCell align="right">{historyRow.tipo}</TableCell>
                                            <TableCell align="right">{historyRow.unidade}</TableCell>
                                            <TableCell align="right">{historyRow.valorUnitario}</TableCell>
                                            <TableCell align="right">{historyRow.coeficiente}</TableCell>
                                            <TableCell align="right">{historyRow.valorItem}</TableCell>
                                        </TableRow>
                                    ))}
                                </TableBody>
                            </Table>
                        </Box>
                    </Collapse>
                </TableCell>
            </TableRow>
        </React.Fragment>
    );
}

const history = [
    {
        tipoItem: 'C',
        codigo: '5678',
        descricao: 'RETROESCAVADEIRA SOBRE RODAS COM CARREGADEIRA, TRAÇÃO 4X4, POTÊNCIA LÍQ. 88 HP, CAÇAMBA CARREG. CAP. MÍN. 1 M3, CAÇAMBA RETRO CAP. 0,26 M3, PESO OPERACIONAL MÍN. 6.674 KG, PROFUNDIDADE ESCAVAÇÃO MÁX. 4,37 M - CHP DIURNO. AF_06/2014',
        tipo: '',
        unidade: 'CHP',
        valorUnitario: '82,07',
        coeficiente: '0,0099',
        valorItem: '0.82',
    },
];

export default function CollapsibleTable({ composicoes }) {

    return (
        <TableContainer component={Paper}>
            <Table aria-label="collapsible table">
                <TableHead>
                    <TableRow>
                        <TableCell />
                        <TableCell>Código</TableCell>
                        <TableCell align="justify">Descrição</TableCell>
                        <TableCell align="right">Tipo&nbsp;</TableCell>
                        <TableCell align="right">Unidade&nbsp;</TableCell>
                        <TableCell align="right">Data&nbsp;</TableCell>
                        <TableCell align="right">Valor&nbsp;</TableCell>
                    </TableRow>
                </TableHead>
                <TableBody>
                    {composicoes.map((row) => (
                        <Row key={row._id} row={row} history={history} />
                    ))}
                </TableBody>
            </Table>
        </TableContainer>
    );
}
