import React, { useEffect, useState } from 'react';
import MaterialTable from 'material-table';

import api from '../../services/api';


export default function MaterialTableDemo(props) {

    const [state, setState] = React.useState({
        columns: [
            { title: 'Código', field: 'codigo', align: 'left' },
            { title: 'Descrição', field: 'descricaoInsumo', align: 'left' },
            { title: 'Unidade', field: 'unidade', editable: 'never', sorting: false, readonly: true, align: 'left' },
            { title: 'Preço', field: 'precoMed', type: 'currency', currencySetting: { currencyCode: 'BRL', locale: 'pt-BR' }, align: 'left' },
        ],
        data: []
    });


    useEffect(() => {
        //loadImoveis();
    }, [])

    async function loadImoveis() {

        console.log(props.filtro)

        const response = await api.get(`/insumos/search?input=${props.filtro}&page=1`);
        setState((prevState) => {
            const data = prevState.data;
            data.push(...response.data);
            console.log(data);
            return { ...prevState };
        });
    }

    return (
        <div>
            <MaterialTable
                title=""
                columns={state.columns}
                data={state.data[0]}
                localization={{
                    body: {
                        emptyDataSourceMessage: 'Nenhum registro para exibir',
                        addTooltip: 'adicionar',
                        deleteTooltip: 'excluir',
                        editTooltip: 'editar',
                        editcancelTooltip: 'cancelar',

                        editRow: {
                            saveTooltip: 'salvar',
                            cancelTooltip: 'cancelar',
                            deleteText: 'Tem certeza de que deseja excluir?'
                        },
                    },
                    grouping: {
                        groupedBy: 'Agrupado por:',
                        placeholder: 'Arraste os cabeçalhos aqui para agrupar!'
                    },
                    toolbar: {
                        searchTooltip: 'Pesquisar',
                        searchPlaceholder: 'Pesquisar',
                        showColumnsTitle: 'Mostrar colunas',
                        addRemoveColumns: 'Adicionar ou Remover Colunas'
                    },
                    pagination: {
                        labelRowsSelect: 'linhas',
                        labelDisplayedRows: '{count} de {from}-{to}',
                        firstTooltip: 'Primeira página',
                        previousTooltip: 'Página anterior',
                        nextTooltip: 'Próxima página',
                        lastTooltip: 'Última página'
                    }
                }}
                options={{
                    grouping: true,
                    columnsButton: true,
                }}
                actions={[
                    {
                        icon: 'refresh',
                        tooltip: 'Refresh Data',
                        isFreeAction: true,
                        onClick: () => loadImoveis(),
                    }
                ]}
            />
        </div>
    );
}
